/***
 *
 * 包含min函数的栈
 *
 * 定义栈的数据结构，请在该类型中实现一个能够得到栈中所含最小元素的min函数（时间复杂度应为O（1））
 */

let dataStack = [];
let minStack = [];

function push(node) {
  dataStack.push(node);
  if (minStack.length === 0 || node < min) {
    minStack.push(node);
  } else {
    minStack.push(min());
  }
}

function pop() {
  minStack.pop();
  return dataStack.pop();
}

function top() {
  let length = dataStack.length;
  return length > 0 && dataStack[length - 1];
}

function min() {
  let length = minStack.length;
  return length > 0 && minStack[length - 1];
}
